  // MARK: -
  // MARK: Public access
  // MARK: -
  
  // MARK: -> Public class methods
  
  public class <#Application#> {
    
    public static let <#Name#> = Notification.Name("Notification.<#Application#>.<#Name#>")

    public class <#ViewController#> {
      public static let <#Name#> = Notification.Name("Notification.<#Application#>.<#ViewController#>.<#Name#>")
